import React from  "react";

const Reacipe = ({ title, calories, image, ingredients }) => {
    return (
    <div>
        <h1 className="title">{title}</h1>
        <ol>
          {ingredients.map(ingredients =>(
            <li>{ingredients.text}</li>
          ))}
        </ol>
        <p>{calories}</p>
        <img src={image} alt="" />
    </div>
  );
};

export default Reacipe;
